import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

//import Calendar from 'react-calendar'
import './calendar1.css';
import moment from 'moment';
import reactDom from 'react-dom';

class Calendar extends React.Component {
    componentDidMount() { 
$(document).ready(function () {
    var date = new Date();
    var today = date.getDate();
    $(".right-button").onClick({ date: date }, next_year);
    $(".left-button").onClick({ date: date }, prev_year);
    $(".month").onClick({ date: date }, month_onClick);
    $("#add-button").onClick({ date: date }, new_event);
    $(".months-row").children().eq(date.getMonth()).addClass("active-month");
    init_calendar(date);
    var events = check_events(today, date.getMonth() + 1, date.getFullYear());
    show_events(events, months[date.getMonth()], today);
  });
  function init_calendar(date) {
    $(".tbody").empty();
    $(".events-container").empty();
    var calendar_days = $(".tbody");
    var month = date.getMonth();
    var year = date.getFullYear();
    var day_count = days_in_month(month, year);
    var row = $("<tr class='table-row'></tr>");
    var today = date.getDate();
    date.setDate(1);
    var first_day = date.getDay();
   for (var i = 0; i < 35 + first_day; i++) {
      var day = i - first_day + 1;
      if (i % 7 === 0) {
        calendar_days.append(row);
        row = $("<tr class='table-row'></tr>");
      }
      if (i < first_day || day > day_count) {
        var curr_date = $("<td class='table-date nil'>" + "</td>");
        row.append(curr_date);
      } else {
        var curr_date = $("<td class='table-date'>" + day + "</td>");
        var events = check_events(day, month + 1, year);
        if (today === day && $(".active-date").length === 0) {
          curr_date.addClass("active-date");
          show_events(events, months[month], day);
        }
        if (events.length !== 0) {
          curr_date.addClass("event-date");
        }
        curr_date.onClick(
          { events: events, month: months[month], day: day },
          date_onClick
        );
        row.append(curr_date);
      }
    }
    calendar_days.append(row);
    $(".year").text(year);
  }

 function days_in_month(month, year) {
    var monthStart = new Date(year, month, 1);
    var monthEnd = new Date(year, month + 1, 1);
    return (monthEnd - monthStart) / (1000 * 60 * 60 * 24);
  }
 function date_onClick(event) {
    $(".events-container").show(250);
    $("#dialog").hide(250);
    $(".active-date").removeClass("active-date");
    $(this).addClass("active-date");
    show_events(event.data.events, event.data.month, event.data.day);
  }
  
function month_onClick(event) {
    $(".events-container").show(250);
    $("#dialog").hide(250);
    var date = event.data.date;
    $(".active-month").removeClass("active-month");
    $(this).addClass("active-month");
    var new_month = $(".month").index(this);
    date.setMonth(new_month);
    init_calendar(date);
  }
  
function next_year(event) {
    $("#dialog").hide(250);
    var date = event.data.date;
    var new_year = date.getFullYear() + 1;
    $("year").html(new_year);
    date.setFullYear(new_year);
    init_calendar(date);
  }
  
function prev_year(event) {
    $("#dialog").hide(250);
    var date = event.data.date;
    var new_year = date.getFullYear() - 1;
    $("year").html(new_year);
    date.setFullYear(new_year);
    init_calendar(date);
  }
  
